  { id: 428, content_en: nil, short_content_en: nil, describable_id: 55, describable_type: "PressRelease", created_at: nil, updated_at: nil, content_nl: "Kruip in de huid van Maria v/d Hoeven (EZ)", short_content_nl: nil },
