  { id: 573, content_en: nil, short_content_en: nil, describable_id: 84, describable_type: "PressRelease", created_at: "2010-09-27 00:00:00", updated_at: nil, content_nl: "Provincies actualiseren duurzame energie scenario?s", short_content_nl: nil },
  { id: 574, content_en: nil, short_content_en: nil, describable_id: 77, describable_type: "PressRelease", created_at: nil, updated_at: nil, content_nl: "Quintel en GasTerra presenteren online rekenmodel voor energie- en klimaatvraagstuk", short_content_nl: nil },
